(101179, 0, 30041975, 'Thảm trải sàn họa tiết Cờ Anh', 'Quốc kì Anh đã trở nên quá quen thuộc trong các sản phẩm thời trang, nội thất. Với thiết kế cổ điển thảm vintage họa tiết Cờ Anh là một trong những sản phẩm được săn đón nhiều nhất tại Craft & More.', 869000.00, '//bizweb.dktcdn.net/thumb/1024x1024/100/090/662/products/tham-vintage-trang-tri-phong-khach-hoa-tiet-co-anh.jpg?v=1625144462673', 'HIDDEN', 35, 69, '2025-11-25 09:23:42', '2025-12-26 01:55:21'),
(101287, 0, 30041975, 'Sổ tay đẹp Flower & Bird', '0', 125000.00, '//bizweb.dktcdn.net/thumb/1024x1024/100/090/662/products/so-tay-dep-vintage.jpg?v=1600401839923', 'ACTIVE', 0, 70, '2025-11-25 09:23:42', '2025-11-25 09:23:42'),
(101607, 0, 30041975, 'Sổ ghi chép classic nâu trung', 'Bạn là người thích sử dụng sổ tay và luôn sưu tầm những cuốn sổ tay độc đáo và mới lạ. Vậy thì không thể nào bỏ qua mẫu sổ tay classic nâu trung này.', 150000.00, '//bizweb.dktcdn.net/thumb/1024x1024/100/090/662/products/thumnail-vgc.jpg?v=1502263950187', 'ACTIVE', 45, 52, '2025-11-25 09:23:42', '2025-11-25 09:23:42'),
(102230, 0, 30041975, 'Khung Ảnh Mini Handmade', 'Khung ảnh mini handmade', 25000.00, '//bizweb.dktcdn.net/thumb/1024x1024/100/090/662/products/khung-tranh-mini-handmade.jpg?v=1628312396957', 'ACTIVE', 5, 98, '2025-11-25 09:23:42', '2025-11-25 09:23:42'),
(103488, 0, 61400357, 'Giấy Mỹ Thuật A4 Cao Cấp Làm Handmade', 'Giấy mỹ thuật A4 trơn, nguyên liệu làm handmade, album ảnh đẹp', 814000.00, '//bizweb.dktcdn.net/thumb/1024x1024/100/090/662/products/giay-my-thuat-avt.jpg?v=1626763978877', 'ACTIVE', 90, 86, '2025-11-25 09:23:42', '2025-11-25 09:23:42'),
(103803, 0, 27840013, 'Hoa tulip vải handmade size nhỏ', 'Chất liệu: vải, bông, giấy, nhựa Phần bông dài 6cm; bông + thân dài 32cm', 35000.00, '//bizweb.dktcdn.net/thumb/1024x1024/100/090/662/products/hoa-tulip-vai-nho-vietgiftcenter.jpg?v=1465189642320', 'ACTIVE', 91, 88, '2025-11-25 09:23:42', '2025-11-25 09:23:42'),
(107093, 0, 30041975, 'Mực dấu trang trí DIY', 'Kích thước hộp 6x6 cm; Rất nhiều màu cho bạn chọn lựa, phù hợp trang trí thủ công thiệp, album diy, lưu bút, dấu tay wedding tree;', 25000.00, '//bizweb.dktcdn.net/thumb/1024x1024/100/090/662/products/32.jpg?v=1468994872220', 'ACTIVE', 75, 11, '2025-11-25 09:23:42', '2025-11-25 09:23:42'),
(108816, 0, 90974625, 'Ví đa năng vỏ bọc hộ chiếu 6 màu basic - quà tặng cao cấp cho doanh nghiệp, cá nhân', 'Nếu bạn đang tìm bao da passport đa năng và theo phong thủy thì đích thị đây là một lựa chọn hợp lý. Chiếc ví dùng hàng ngày hoặc cho chuyến du lịch đều phù hợp với 6 màu basic rất dễ thương.', 399000.00, '//bizweb.dktcdn.net/thumb/1024x1024/100/090/662/products/vi-ho-chieu-da-that-qua-tang-doanh-nghiep-door-gift-1.jpg?v=1712996597337', 'ACTIVE', 90, 80, '2025-11-25 09:23:42', '2025-11-25 09:23:42'),
(109573, 0, 30041975, 'Thiệp chúc mừng THAT TIME', 'Lấy cảm hứng từ những tấm bìa carton, giấy mỹ thuật, mẫu thiệp này được Craft & More Vietnam sáng tạo theo phong cách hoài cổ vintage, retro rất được yêu thích. Màu nâu kết hợp với đỏ cam, sự thô ráp của bìa carton với sự lãng mạn, nhẹ nhàng của hoa lá cỏ khô tạo nên sự khác biệt cho tấm thiệp.', 70000.00, '//bizweb.dktcdn.net/thumb/1024x1024/100/090/662/products/thiep-valentine-chuc-mung-14-2-mau-dep-lam-handmade-tang-ban-trai-ban-gai-vo-nguoi-yeu-vintage-ban-chay-nhat-hcm-ha-noi-2023-3.jpg?v=1675652629243', 'ACTIVE', 81, 65, '2025-11-25 09:23:42', '2025-11-25 09:23:42'),
(110845, 0, 30041975, 'Tượng trang trí Không nói - Không nghe - Không thấy', '0', 350000.00, '//bizweb.dktcdn.net/thumb/1024x1024/100/090/662/products/tuong-trangt-tri-khong-noi-khong-nghe-khong-noi-2.jpg?v=1614762182777', 'ACTIVE', 1, 42, '2025-11-25 09:23:42', '2025-11-25 09:23:42'),
(112212, 0, 90974625, 'Vỏ bọc hộ chiếu trắng retro', 'Ví hộ chiếu được KHẮC TÊN - GẮN CHARM trang trí theo ý riêng: trẻ trung, năng động, nhiệt huyết nhưng lại nhẹ nhàng với màu trắng; chiếc vỏ bọc hộ chiếu sẽ theo bạn trên những chuyến đi.', 150000.00, '//bizweb.dktcdn.net/thumb/1024x1024/100/090/662/products/vo-boc-ho-chieu-hn.jpg?v=1546489405247', 'ACTIVE', 20, 67, '2025-11-25 09:23:42', '2025-11-25 09:23:42'),
(112787, 0, 90974625, 'Túi đựng hộ chiếu màu đen D8', 'Vỏ bọc hộ chiếu rất phù hợp làm quà tặng các sự kiện, doorgift; tặng khách hàng, nhân viên. Gọi ngay 0903 803 556 để in logo, thông tin cá nhân hóa ví đựng passport giá tốt nhất cho doanh nghiệp.', 180000.00, '//bizweb.dktcdn.net/thumb/1024x1024/100/090/662/products/tui-dung-ho-chieu-mau-den-d8-2.jpg?v=1721961322693', 'ACTIVE', 73, 96, '2025-11-25 09:23:42', '2025-11-25 09:23:42'),
(112840, 0, 30041975, 'Bao lì xì \"Để Gió Cuốn Đi\"', 'Mặt trước bao lì xì độc đáo này', 15000.00, '//bizweb.dktcdn.net/thumb/1024x1024/100/090/662/products/bao-li-xi-xong-trong-doi-song.jpg?v=1513322955870', 'ACTIVE', 20, 54, '2025-11-25 09:23:42', '2025-11-25 09:23:42'),
